import Card from '../ui/Card'
import classes from './GenshinCharacter.module.css'
import { useRef } from 'react'

function GenshinCharacter(props) {
  const nameInputRef = useRef();
  const elementInputRef = useRef();
  const levelInputRef = useRef();
  const starsInputRef = useRef();
  const descriptionInputRef = useRef();

  function deleteHandler(props) {
    event.preventDefault();

    const enteredName = nameInputRef.current.value;
    const enteredElement = elementInputRef.current.value;
    const enteredLevel = levelInputRef.current.value;
    const enteredStars = starsInputRef.current.value;
    const enteredDescription = descriptionInputRef.current.value;

    const characterData = {
      name: enteredName,
      element: enteredElement,
      level: enteredLevel,
      stars: enteredStars,
      description: enteredDescription,
    }

    props.onAddChar(characterData)
  }
  return (
    <li className={classes.item}>
      <Card>
        <div className={classes.content}>
          <h3>{props.name}</h3>
          <p>Element: {props.element}</p>
          <p>Level: {props.level}</p>
          <p>Stars: {props.stars}</p>
          <p>{props.description}</p>
        </div>
        <div className={classes.actions}>
          <button onClick={deleteHandler}>Delete</button>
        </div>
      </Card>
    </li>
  )
}

export default GenshinCharacter
